"use strict";var Colors=function(){var colorButton=document.querySelector("[data-button-colors]");var lightButton=document.querySelector("[data-button-dark]");var darkButton=document.querySelector("[data-button-light]");var cover=document.querySelector("[data-cover]");function init(){bindEvents()}function bindEvents(){colorButton.addEventListener("click",function(){return toggleColor(true)});darkButton.addEventListener("click",function(){return toggleColor(false)});lightButton.addEventListener("click",function(){return toggleColor(true)})}function toggleColor(condition){if(condition){cover.classList.add("has-color")}else{cover.classList.remove("has-color")}}return{init:init}}();Colors.init();