"use strict";var Colors=function(){var hold=document.querySelector("[data-hold]");var colorButton=document.querySelector("[data-button-colors]");var lightButton=document.querySelector("[data-button-dark]");var darkButton=document.querySelector("[data-button-light]");var cover=document.querySelector("[data-cover]");function init(){bindEvents()}function bindEvents(){colorButton.addEventListener("click",function(){return toggleCoverColor(true)});darkButton.addEventListener("click",function(){return toggleCoverColor(false)});lightButton.addEventListener("click",function(){toggleTheme();toggleCoverColor(true)})}function toggleTheme(){hold.classList.toggle("is-light")}function toggleCoverColor(condition){if(condition){cover.classList.add("has-color")}else{cover.classList.remove("has-color")}}return{init:init}}();Colors.init();